package com.jusfoun.jusfouninquire.net.model;import java.io.Serializable;/** * @author henzil * @version create time:2013-7-22_下午1:59:20 * @E-mail lizhen@dns.com.cn * @Description 所有model 基类。 */public class BaseModel implements Serializable {    /**     *     */    private static final long serialVersionUID = 5268625605268545266L;    private int code;    private int result = -1;    private String msg;    private String message;    public boolean isCache;    public String cacheKey;    public String getMessage() {        return message;    }    public void setMessage(String message) {        this.message = message;    }    public int getResult() {        return result;    }    public boolean success() {        return result == 0;    }    public void setResult(int result) {        this.result = result;    }    public static long getSerialversionuid() {        return serialVersionUID;    }    public int getCode() {        return code;    }    public void setCode(int code) {        this.code = code;    }    public String getMsg() {        return msg;    }    public void setMsg(String msg) {        this.msg = msg;    }}